const { ethers } = require("ethers");

// Transaction data (full raw data including selector)
const fullData =
  "";

// ABI for the `claim` function
const abi = [
  "function claim(uint256 fid, address to, uint256 amount, bytes32[] proof, bytes sig)"
];

// Initialize ethers interface
const iface = new ethers.Interface(abi);

try {
  // Decode the function data
  const decoded = iface.decodeFunctionData("claim", fullData);

  // Output decoded parameters
  console.log("Decoded Parameters:", decoded);
} catch (error) {
  console.error("Decoding Error:", error);
}
